import React, { useContext } from "react";
import { Link } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { ThemeContext } from "../../context";
import { toggleTheme } from "../../slices/themeSwitchSlices";
import styles from "./TopOfSite.module.scss";
import LogoWhiteDiamond from "../../logo/LogoWhiteDiamond.webp";
import LogoSun from "../../logo/LogoSun.jpg";
import LogoMoon from "../../logo/LogoMoon.jpg";

const TopOfSite = () => {
  const { useSetClassName } = useContext(ThemeContext);
  const { classNameHeader } = useSetClassName();
  const theme = useSelector((state) => state.themes.theme);
  const dispatch = useDispatch();
  const toggleLogo = () => (theme ? LogoSun : LogoMoon);

  return (
    <>
      <div className={classNameHeader()}>
        <nav className={styles.navContainer}>
          <ul className={styles.ulContainer}>
            <Link sr to="/"> {" "}<img className={styles.logoWhiteDiamond} src={LogoWhiteDiamond}></img>
              Project
            </Link>
            <li>Todo List</li>
            <li>
              <Link to="/userRegistration">Registration User</Link>
            </li>
            <li>Ches</li>
            <img
              onClick={() => dispatch(toggleTheme())}
              className={styles.logoSun_Moon}
              src={toggleLogo()}
            />
          </ul>
        </nav>
      </div>
    </>
  );
};

export default TopOfSite;
