import React from 'react';
import cx from "classnames";
import styles from '../common/styles/theme.module.scss'
import { useSelector } from 'react-redux';

const useSetClassName = () => {
  const isDark = useSelector((state)=>state.themes.theme);

  return{ 
    classNamePage : ()=>{
    const className = cx(styles.darkPage,{
   [styles.lightPage]: isDark === false,});
 return className;},
      classNameHeader: ()=>{
        const className = cx(styles.darkHeader,{
          [styles.lightHeader]: isDark === false,});
        return className;
      }
}
}

export default useSetClassName;
