import { createSlice } from "@reduxjs/toolkit";
import cx from 'classnames';
import styles from "../common/styles/_template.scss";



const initialState = { 
  isDark : true,
};

const toggleTheme = createSlice({
name: "themes",
initialState,
reducers:{
  themeSwitch: (state,action)=>{
   state.isDark = !state.isDark;
  },

}
});

export const { themeSwitch} = themeSwitchSlice.actions;
export default themeSwitchSlice.reducer;
