import { createSlice } from "@reduxjs/toolkit";
import cx from "classnames";
import styles from "../common/styles/_template.scss";




const initialState = { 
  isDark : true,
  theme: styles.dark
};

const themeSwitchSlice = createSlice({
name: "themes",
initialState,
reducers:{
  toggleTheme: (state,action)=>{
   state.isDark = !state.isDark;
   if(state.isDark === false){
    state.theme = styles
   }
   state.theme = cx(state.theme,{
    [action.dark]: state.isDark === false,
  });

  },

}
});

export const { toggleTheme} = themeSwitchSlice.actions;
export default themeSwitchSlice.reducer;
