import React from 'react';
import {Formik, Field, Form} from "formik";
import {useDispatch} from 'react-redux';
import {createUserSuccess, createUserRequest} from '../../slices/userSlices';

const UserForm = (props) => {
  const dispatch = useDispatch();
  const onSubmit = (values,formikBag)=>{
    console.log(values);
    dispatch(createUserRequest(values));
    formikBag.resetForm();
  };
  return (
   <Formik onSubmit={onSubmit} initialValues={{login:'',email:'',password:'',imgPath:''}} >
   {(formikPorops)=>  (
      <Form encType='multipart/form-data' >
      <Field name='login' placeholder='login' />
      <Field name='email' placeholder='email' />
      <Field name='password' type='password' placeholder='password' />
      <input type='file' name='imgPath' placeholder='img' onChange={({target})=>formikPorops.setFieldValue("imgPath",target.files[0])} />
      <input type='submit' value='Add user' />
    </Form>)}
   </Formik>
  );
}

export default UserForm;
