import * as Yup from 'yup';

const SCHEMA_NAME = Yup.string().matches(/[a-zA-Z0-9].{1,64}$/,'name ');
const SCHEMA_EMAIL = Yup.string().matches(/[^@ \t\r\n]+@[^@ \t\r\n]+\.[^@ \t\r\n]+/);
const SCHEMA_PASSWORD = Yup.string().matches(/^(?=.*?[A-Z])(?=.*?[a-z]).{5,}$/);


export const SCHEMA_SIGN_UP_FORM = Yup.object({
  name: SCHEMA_NAME,
  email: SCHEMA_EMAIL,
  password: SCHEMA_PASSWORD,
});
