import React, { useContext, useEffect } from 'react';
import {useDispatch, useSelector} from "react-redux";
import {Field, Form, Formik} from "formik";
import styles from '../../common/styles/all.module.scss';
import {createUserRequest} from "../../slices/userSlices";
import {toggleTheme} from "../../slices/themeSwitchSlices";
import { ThemeContext } from '../../context';

const UserRegistration = () => {
  const {ThemeContext} = useContext(ThemeContext);
  const dispath = useDispatch();
  const isDark = useSelector((state)=>state.themes.isDark);

  const onSubmit = (values, formikBag)=>{
    dispath(createUserRequest(values))
    console.log(values);
    formikBag.resetForm();
  };
  
  useEffect(()=>{
    className()
  },[isDark]);
 



  return (
    <><div className={className()}> <div  className={styles.form}>
          <Formik  onSubmit={onSubmit} initialValues={{firstName: '',lastName: '',email: '',password: '',birthday:''}}>
      <Form >
      <Field name="firstName" placeholder='firstName' />
      <Field name="lastName" placeholder='lastName' />
      <Field name="email" placeholder='email' />
      <Field name="password" placeholder='password' />
      <Field type='date' name="birthday" placeholder='birthday' />
      <Field name='submit' type='submit' value='send' />
      </Form>
    </Formik>
    <button onClick={()=>dispath(toggleTheme())}>Switch</button>
     </div></div>
    </>
   

  );
}
export default UserRegistration;
