import React from 'react';
import cx from "classnames";
import styles from '../common/styles/all.scss'
import { useSelector } from 'react-redux';

const useSetClassName = () => {
  const isDark = useSelector((state)=>state.themes.isDark);

  // const className = 
  return{ 
    className : ()=>{
    const className = cx(styles.dark,{
   [styles.light]: isDark === false,});
 return className;}
}
}

export default useSetClassName;
